            if (!scrollDist) return;

            scrollDir = prevScrollTop < scrollTop ? 1 : -1;
            prevScrollTop = scrollTop;

            // switch virtual pages if needed
            if (scrollDist < viewportH) {
                scrollTo(scrollTop + offset);
            }
